    mutation AddTeam(
        $Name: String!,
        $members: Number!,
        $project:String!
        $task: String!,
        $description: String!,
        ) {
        addTeam(
            Name: $Name,
            members:$members
            project: $project,
            task: $task,
            description: $description,
           ) {
            _id
        }
    }
`;

